// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Certificate.sol";
import "./Student.sol";

contract Verifier {
    address public admin;
    Certificate public certificateContract;
    Student public studentContract;

    constructor() {
        admin = msg.sender;
    }

    function setContracts(address _certificate, address _student) public {
        require(msg.sender == admin, "Only admin can set contracts");
        certificateContract = Certificate(_certificate);
        studentContract = Student(_student);
    }

    function issueCertificateToStudent(
        address student,
        string memory name,
        string memory subject,
        string memory ipfsHash
    ) public {
        require(studentContract.isRegistered(student), "Student not registered");
        certificateContract.issueCertificate(student, name, subject, ipfsHash);
    }
}
